// src/api.js
import axios from 'axios';

// Axios 인스턴스 생성
const api = axios.create({
  baseURL: process.env.VUE_APP_BACKEND_URL,
  withCredentials: true, // 모든 요청에 쿠키를 포함하도록 설정
});

export default api;
